import React from 'react';
import { ClipLoader } from 'react-spinners';

import GAContent from './GAContent';

class GAQuestion extends React.Component {
    _mounted = false;

    constructor(props) {
        super(props);
        this.getQuestion = this.getQuestion.bind(this);
        this.removeQuestion = this.removeQuestion.bind(this);
        this.state = { questionAvailable: false, currentQueue: [] };
    }

    // getting a question
    async getQuestion() {
        await fetch('http://localhost:5000/getActorAPI', { signal: this.props.controller.signal }).then(res => res.json()).then(data => {
            if (this._mounted === true) {
                var indexCheck = this.state.currentQueue.indexOf(data.actorID);
                if (indexCheck === -1) {
                    var newQueue = this.state.currentQueue;
                    newQueue.push(data.actorID);
                    this.setState({ currentQueue: newQueue} );
                    this.props.enqueue(data);
                    this.setState({ questionAvailable: true });
                } else {
                    this.getQuestion();
                    alert("called again");
                }
            }
        });


        /*if (this.props.queue.length === 0) {
            this.setState({ questionAvailable: false});
        }
        if (this._mounted === true) {
            this.fetchQuestion();
        }*/
        // redo fetch!! - look at old version of app
        /*if (this._mounted === true) {
            await fetch('https://bigfatmoviequiz.herokuapp.com/getActorAPI', {signal: this.props.controller.signal}).then(res => {
                res.json();
            }).then(data => {
                alert(data);
                var indexCheck = this.state.currentQueue.indexOf(data.actorID);
                if (indexCheck === -1) {
                    var newQueue = this.state.currentQueue;
                    newQueue.push(data.actorID);
                    this.setState({ currentQueue: newQueue} );
                    this.props.enqueue(data);
                    this.setState({ questionAvailable: true });
                } else {
                    this.getQuestion();
                    alert("called again");
                }
                
            }).catch((error) => {
                this.getQuestion();
            });
        }*/
    }

    removeQuestion() {
        if (this.props.queue.length === 1) {
            this.setState({ questionAvailable: false});
        }
        this.props.dequeue();
    }

    componentDidMount() { // onload of the component
        
        this._mounted = true;
        if (this.props.queue.length === 0) {
            this.getQuestion();
            this.getQuestion();
            this.getQuestion();
        } else {
            this.setState({ questionAvailable: true});
        }
    }

    componentWillUnmount() {
        //this.controller.abort();
        this._mounted = false;
    }

    render() {
        return(
            <>
                {this.state.questionAvailable ? <GAContent 
                                                    question={this.props.queue[0]} 
                                                    gameOver={this.props.gameOver} 
                                                    increaseScore={this.props.increaseScore}
                                                    resetScore={this.props.resetScore}
                                                    removeQuestion={this.removeQuestion} 
                                                    newQuestion={this.getQuestion}
                                                    score={this.props.score} /> 
                                                    : 
                                                    <div className="contentBox">
                                                        <div class="fade-in">
                                                            <div class="spinner-border text-light" role="status">
                                                            </div>
                                                        </div>
                                                    </div>
                                                    }
            </>
        )
    }
}

export default GAQuestion;
